/* OracleOEM */ 
select * from (
select 
  substr(SYS_CONNECT_BY_PATH(c, '->'),3,512)  path, c, 'ANY_PRIVILEGE'
from
  (
   select 
      null   p, 
      name   c
    from 
      system_privilege_map
    where
      name like '%ANY%'
  union
    select 
      granted_role  p,
      grantee       c
    from
      dba_role_privs
  union
    select
      privilege     p,
      grantee       c
    from
      dba_sys_privs
  )
  where ((c = 'PUBLIC') or (exists (select 'w' from dba_users where username=c))) 
start with p is null
connect by p = prior c )
where rownum < 200
;

